mapper.select:
select id id,
trade_price tradePrice,
trade_type tradeType,
trade_msg tradeMsg,
status from trade_info where 
delete_status = '1'

	and id = #{id}


	and trade_price = #{tradePrice},


	and trade_type = #{tradeType}


	and trade_msg = #{tradeMsg}


	and task_id = #{taskId}


	and status = #{status}

select count(1) from trade_info where 
delete_status = '1'

	and id = #{id}


	and trade_price = #{tradePrice},


	and trade_type = #{tradeType}


	and trade_msg = #{tradeMsg}


	and task_id = #{taskId}


	and status = #{status}

mapper.insert:
insert into trade_info(trade_price, trade_type, trade_msg, task_id, status, delete_status)
values(
	#{tradePrice},#{tradeType},#{tradeMsg},#{taskId},#{status},#{deleteStatus}
)

mapper.update:
update trade_info 

	
		id = #{id},
	
	
		trade_price = #{tradePrice},
	
	
		trade_type = #{tradeType},
	
	
		trade_msg = #{tradeMsg},
	
	
		task_id = #{taskId},
	
	
		status = #{status}
	
	
		delete_status = #{deleteStatus}
	

	where id = #{id}
